(27, 4, 19, 'Xtep Giày chạy nam', '<ul>\r\n	<li>\r\n	<h3>Chất liệu vải dệt thể thao v&agrave; da PU bền chắc</h3>\r\n	</li>\r\n	<li>\r\n	<h3>Thiết kế mũi hớt cao hỗ trợ tối ưu mọi chuyển động</h3>\r\n	</li>\r\n	<li>\r\n	<h3>Đế cao su mềm, c&oacute; r&atilde;nh chống trơn trượt</h3>\r\n	</li>\r\n	<li>\r\n	<h3>Xuất xứ: Trung Quốc</h3>\r\n	</li>\r\n</ul>\r\n', '<h4><strong>Gi&agrave;y chạy nam Xtep 982219116965-1 m&agrave;u xanh dương</strong>&nbsp;được sản xuất từ chất liệu vải thể thao chuy&ecirc;n dụng kết hợp với c&aacute;c c&ocirc;ng nghệ ti&ecirc;n tiến, hỗ trợ tối ưu mọi vận động. Gi&agrave;y c&oacute; lớp l&oacute;t trong&nbsp;&ecirc;m mềm, tho&aacute;ng kh&iacute;, vừa ngăn m&ugrave;i, vừa kh&ocirc;ng g&acirc;y b&iacute; ch&acirc;n khi chạy. Đế cao su nhẹ bền, c&oacute; độ đ&agrave;n hồi tốt gi&uacute;p giảm tối đa chấn thương khi bạn tập luyện hoặc chơi thể thao. Kh&ocirc;ng chỉ l&agrave; phụ kiện cần thiết cho c&aacute;c buổi tập luyện, gi&agrave;y Xtep c&ograve;n gi&uacute;p bạn ho&agrave;n thiện vẻ ngo&agrave;i c&aacute; t&iacute;nh v&agrave; phong c&aacute;ch trẻ trung.&nbsp;</h4>\r\n\r\n<h4><strong>Ch&iacute;nh s&aacute;ch bảo h&agrave;nh</strong></h4>\r\n\r\n<ul>\r\n	<li>\r\n	<h4>Thời gian đổi trong v&ograve;ng 3 ng&agrave;y với c&aacute;c trường hợp (Đổi size, đổi m&agrave;u sắc, kiểu d&aacute;ng)</h4>\r\n	</li>\r\n	<li>\r\n	<h4>Điều kiện: Sản phẩm c&ograve;n nguy&ecirc;n tem m&aacute;c, chưa qua sử dụng v&agrave; sản phẩm phải c&oacute; h&oacute;a đơn mua h&agrave;ng k&egrave;m theo</h4>\r\n	</li>\r\n	<li>\r\n	<h4>Chỉ được đổi một lần duy nhất</h4>\r\n	</li>\r\n</ul>\r\n\r\n<h4><strong>Hướng dẫn bảo quản</strong></h4>\r\n\r\n<ul>\r\n	<li>\r\n	<h4>Giặt với nước ở nhiệt độ thường</h4>\r\n	</li>\r\n	<li>\r\n	<h4>Kh&ocirc;ng giặt m&aacute;y</h4>\r\n	</li>\r\n	<li>\r\n	<h4>Kh&ocirc;ng sử dụng chất tẩy mạnh</h4>\r\n	</li>\r\n	<li>\r\n	<h4>Tr&aacute;nh phơi trực tiếp dưới &aacute;nh nắng gắt</h4>\r\n	</li>\r\n</ul>\r\n\r\n<h4><strong>Th&ocirc;ng tin thương hiệu</strong></h4>\r\n\r\n<h4>Được th&agrave;nh lập v&agrave;o năm 1999 tại Trung Qu&ocirc;́c, cho đ&ecirc;́n nay Xtep đã trở thành m&ocirc;̣t trong những thương hi&ecirc;̣u thời trang thể thao uy t&iacute;n thế giới. Hiện tại, Xtep đang quản l&yacute; một mạng lưới ph&acirc;n phối rộng lớn với hơn 7.000 cửa h&agrave;ng b&aacute;n lẻ th&ocirc;ng qua các nh&agrave; ph&acirc;n phối độc quyền, bao gồm 31 tỉnh, khu tự trị v&agrave; th&agrave;nh phố tr&ecirc;n khắp Trung Quốc. Những sản ph&acirc;̉m của Xtep được đ&ocirc;ng đảo các tín đ&ocirc;̀ th&ecirc;̉ thao tr&ecirc;n th&ecirc;́ giới, các c&acirc;̀u thủ chuy&ecirc;n nghi&ecirc;̣p, cũng như các di&ecirc;̃n vi&ecirc;n n&ocirc;̉i ti&ecirc;́ng tin dùng và lựa chọn. Dấu ch&acirc;n to&agrave;n cầu của Xtep trải d&agrave;i ở khắp Trung Đ&ocirc;ng, Trung &Acirc;u v&agrave; T&acirc;y Ban Nha.</h4>\r\n', '1536413446153640973115363925798.png', 1, 20, 'Xanh Dương', '39,40,41,42,43', 950000),
(28, 4, 19, 'Anta Giày chạy nam', '<ul>\r\n	<li>\r\n	<h3>Chất liệu vải lưới thể thao chuy&ecirc;n dụng, da tổng hợp nhẹ, bền, tho&aacute;ng kh&iacute;</h3>\r\n	</li>\r\n	<li>\r\n	<h3>Thiết kế mũi hớt nhẹ, cổ cao vừa</h3>\r\n	</li>\r\n	<li>\r\n	<h3>Đế cao su nhẹ, chống trơn trượt</h3>\r\n	</li>\r\n	<li>\r\n	<h3>Xuất xứ: Trung Quốc</h3>\r\n	</li>\r\n</ul>\r\n', '<h4><strong>Gi&agrave;y chạy nam Anta 81825521-4</strong>&nbsp;l&agrave; sản phẩm được thiết kế chuy&ecirc;n biệt với kiểu d&aacute;ng &ocirc;m cổ ch&acirc;n, đế gi&agrave;y bằng cao su nhẹ, chắc chắn v&agrave; đ&agrave;n hồi, n&acirc;ng đỡ tối đa b&agrave;n ch&acirc;n v&agrave; cổ ch&acirc;n trong khi chạy. Mặt tr&ecirc;n được l&agrave;m từ chất liệu vải lưới thể thao chuy&ecirc;n dụng phối da tổng hợp nhẹ, bền, tho&aacute;ng kh&iacute;. Mũi gi&agrave;y hớt nhẹ, đế gi&agrave;y b&aacute;m s&agrave;n, chống trơn trượt hiệu quả. Kh&ocirc;ng chỉ l&agrave; phụ kiện cần thiết cho c&aacute;c buổi tập luyện, gi&agrave;y Anta c&ograve;n gi&uacute;p bạn ho&agrave;n thiện vẻ ngo&agrave;i c&aacute; t&iacute;nh v&agrave; phong c&aacute;ch trẻ trung.&nbsp;</h4>\r\n\r\n<h4><img alt="Giày chạy bộ Anta" src="https://media.static-adayroi.com/sys_master/h36/hb8/16084442742814.jpg" /></h4>\r\n\r\n<h4><strong>Hướng dẫn bảo quản</strong></h4>\r\n\r\n<ul>\r\n	<li>\r\n	<h4>Lau hoặc giặt sạch vết bẩn với nước</h4>\r\n	</li>\r\n	<li>\r\n	<h4>Kh&ocirc;ng giặt m&aacute;y, kh&ocirc;ng d&ugrave;ng thuốc tẩy</h4>\r\n	</li>\r\n	<li>\r\n	<h4>Kh&ocirc;ng phơi sản phẩm nơi &aacute;nh nắng gắt</h4>\r\n	</li>\r\n	<li>\r\n	<h4>Để sản phẩm nơi kh&ocirc; r&aacute;o khi kh&ocirc;ng sử dụng</h4>\r\n	</li>\r\n</ul>\r\n\r\n<h4><strong>Th&ocirc;ng tin thương hiệu</strong></h4>\r\n\r\n<h4>Anta l&agrave; m&ocirc;̣t trong những thương hiệu thời trang thể thao h&agrave;ng đầu Trung Quốc chuy&ecirc;n sản xuất v&agrave; kinh doanh quần &aacute;o, gi&agrave;y thể thao v&agrave; phụ kiện đi k&egrave;m. Với slogan &quot;Keep Moving....&quot; thể hiện sự vận động, phấn đấu kh&ocirc;ng ngừng v&agrave; ph&aacute;t triển đi l&ecirc;n; c&ugrave;ng t&ocirc;n chỉ &ldquo;Th&agrave;nh thật, chữ t&iacute;n, thương hiệu trăm năm&rdquo;, Anta được vinh danh l&agrave; &ldquo;Thương hiệu nổi tiếng Trung Quốc&rdquo;, &ldquo;Nh&atilde;n hiệu nổi bật Trung Quốc&rdquo;, v&agrave; l&agrave; &ldquo;Sản phẩm miễn kiểm tra chất lượng&rdquo;. Anta chiếm vị tr&iacute; &ldquo;Thương hiệu thời trang thể thao bậc nhất trong bảng gi&aacute; trị thương hiệu&quot; (do tạp ch&iacute; Forbes c&ocirc;ng bố).</h4>\r\n', '153641342315363926799.png', 0, 10, 'Xanh Dương', '39,40,41,42,43', 800000),
